import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useMsal } from '@azure/msal-react';
import { toast } from 'react-hot-toast';
import {
  useReactTable,
  getCoreRowModel,
  flexRender,
  createColumnHelper,
} from '@tanstack/react-table';
import { ChevronLeftIcon, MagnifyingGlassIcon } from '@heroicons/react/24/outline';
import { apiService } from '../services/apiService';

const TableView = () => {
  const { tableName } = useParams();
  const { instance, accounts } = useMsal();
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [pagination, setPagination] = useState({
    page: 1,
    pageSize: 20,
    totalPages: 0,
    total: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingCell, setEditingCell] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [filterColumn, setFilterColumn] = useState('');
  const [filterValue, setFilterValue] = useState('');
  const [columns, setColumns] = useState([]);

  // Fetch table data
  const fetchData = async () => {
    setLoading(true);
    try {
      const params = {
        page: pagination.page,
        page_size: pagination.pageSize,
      };

      if (filterColumn && filterValue) {
        params.filter_column = filterColumn;
        params.filter_value = filterValue;
      }

      const response = await apiService.getTableData(
        instance,
        accounts[0],
        tableName,
        params
      );

      setData(response.data);
      setPagination({
        ...pagination,
        total: response.total,
        totalPages: response.total_pages,
      });

      // Dynamically create columns from the first row
      if (response.data.length > 0) {
        const firstRow = response.data[0];
        const columnHelper = createColumnHelper();

        const tableColumns = Object.keys(firstRow).map((key) => {
          return columnHelper.accessor(key, {
            header: key,
            cell: (info) => {
              const value = info.getValue();
              const rowIndex = info.row.index;
              const columnId = info.column.id;

              const isEditing = 
                editingCell && 
                editingCell.rowIndex === rowIndex && 
                editingCell.columnId === columnId;

              if (isEditing) {
                return (
                  <input
                    type="text"
                    value={editValue}
                    onChange={(e) => setEditValue(e.target.value)}
                    onBlur={handleCellUpdate}
                    onKeyDown={(e) => {
                      if (e.key === 'Enter') {
                        handleCellUpdate();
                      } else if (e.key === 'Escape') {
                        setEditingCell(null);
                      }
                    }}
                    className="w-full p-1 border border-primary-300 focus:outline-none focus:ring-1 focus:ring-primary-500"
                    autoFocus
                  />
                );
              }

              return (
                <div
                  className="p-1 cursor-pointer hover:bg-gray-100"
                  onClick={() => handleCellClick(value, rowIndex, columnId)}
                >
                  {value !== null && value !== undefined ? value.toString() : ''}
                </div>
              );
            },
          });
        });

        setColumns(tableColumns);
      }
    } catch (err) {
      console.error('Error fetching table data:', err);
      setError(err.message);
      toast.error(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [tableName, pagination.page, pagination.pageSize, instance, accounts]);

  // Handle cell click for editing
  const handleCellClick = (value, rowIndex, columnId) => {
    // Don't allow editing the ID column
    if (columnId === 'id') return;

    setEditingCell({ rowIndex, columnId });
    setEditValue(value !== null && value !== undefined ? value.toString() : '');
  };

  // Handle cell update
  const handleCellUpdate = async () => {
    if (!editingCell) return;

    const { rowIndex, columnId } = editingCell;
    const rowData = data[rowIndex];
    const rowId = rowData.id;

    // If value hasn't changed, just exit edit mode
    if (rowData[columnId].toString() === editValue) {
      setEditingCell(null);
      return;
    }

    // Prepare update data
    const updates = {
      [columnId]: editValue
    };

    try {
      toast.loading('Updating data...');
      await apiService.updateRow(instance, accounts[0], tableName, rowId, updates);

      // Update local data
      const newData = [...data];
      newData[rowIndex] = {
        ...newData[rowIndex],
        [columnId]: editValue
      };
      setData(newData);

      toast.dismiss();
      toast.success('Data updated successfully');
    } catch (err) {
      console.error('Error updating cell:', err);
      toast.dismiss();
      toast.error(`Failed to update data: ${err.message}`);
    } finally {
      setEditingCell(null);
    }
  };

  // Handle filter submission
  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchData();
  };

  // Handle filter reset
  const handleFilterReset = () => {
    setFilterColumn('');
    setFilterValue('');
    // Reset to first page when clearing filters
    setPagination({
      ...pagination,
      page: 1
    });
    // Fetch data without filters
    fetchData();
  };

  // Create table instance
  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
  });

  if (error && error.includes('access')) {
    return (
      <div className="bg-white shadow overflow-hidden sm:rounded-lg p-6">
        <div className="flex items-center mb-6">
          <button
            onClick={() => navigate('/')}
            className="flex items-center text-primary-600 hover:text-primary-900"
          >
            <ChevronLeftIcon className="h-5 w-5 mr-1" />
            Back to Tables
          </button>
        </div>
        <div className="text-center py-12">
          <div className="text-red-500 text-xl mb-4">Access Denied</div>
          <p className="text-gray-500">
            You don't have permission to access this table.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white shadow overflow-hidden sm:rounded-lg">
      <div className="px-4 py-5 sm:px-6 flex justify-between items-center">
        <div>
          <button
            onClick={() => navigate('/')}
            className="flex items-center text-primary-600 hover:text-primary-900 mb-2"
          >
            <ChevronLeftIcon className="h-5 w-5 mr-1" />
            Back to Tables
          </button>
          <h3 className="text-lg leading-6 font-medium text-gray-900">{tableName}</h3>
        </div>

        {/* Filter form */}
        <form onSubmit={handleFilterSubmit} className="flex items-center space-x-2">
          <div className="flex items-center">
            <select
              value={filterColumn}
              onChange={(e) => setFilterColumn(e.target.value)}
              className="block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-primary-500 focus:border-primary-500 sm:text-sm rounded-md"
            >
              <option value="">Select column</option>
              {columns.map((column) => (
                <option key={column.id} value={column.id}>
                  {column.id}
                </option>
              ))}
            </select>
          </div>
          <div className="relative flex items-center">
            <input
              type="text"
              value={filterValue}
              onChange={(e) => setFilterValue(e.target.value)}
              placeholder="Filter value..."
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-primary-500 focus:border-primary-500 sm:text-sm"
            />
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
          </div>
          <button
            type="submit"
            className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            Filter
          </button>
          <button
            type="button"
            onClick={handleFilterReset}
            className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            Reset
          </button>
        </form>
      </div>

      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
        </div>
      ) : (
        <>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                {table.getHeaderGroups().map((headerGroup) => (
                  <tr key={headerGroup.id}>
                    {headerGroup.headers.map((header) => (
                      <th
                        key={header.id}
                        scope="col"
                        className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                      >
                        {flexRender(
                          header.column.columnDef.header,
                          header.getContext()
                        )}
                      </th>
                    ))}
                  </tr>
                ))}
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {table.getRowModel().rows.map((row) => (
                  <tr key={row.id}>
                    {row.getVisibleCells().map((cell) => (
                      <td
                        key={cell.id}
                        className="px-6 py-4 whitespace-nowrap text-sm text-gray-500"
                      >
                        {flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext()
                        )}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Pagination */}
          <div className="bg-white px-4 py-3 flex items-center justify-between border-t border-gray-200 sm:px-6">
            <div className="flex-1 flex justify-between sm:hidden">
              <button
                onClick={() => setPagination({...pagination, page: Math.max(1, pagination.page - 1)})}
                disabled={pagination.page === 1}
                className={`relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 ${
                  pagination.page === 1 ? 'opacity-50 cursor-not-allowed' : ''
                }`}
              >
                Previous
              </button>
              <button
                onClick={() => setPagination({...pagination, page: Math.min(pagination.totalPages, pagination.page + 1)})}
                disabled={pagination.page === pagination.totalPages}
                className={`ml-3 relative inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 ${
                  pagination.page === pagination.totalPages ? 'opacity-50 cursor-not-allowed' : ''
                }`}
              >
                Next
              </button>
            </div>
            <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
              <div>
                <p className="text-sm text-gray-700">
                  Showing <span className="font-medium">{(pagination.page - 1) * pagination.pageSize + 1}</span> to{' '}
                  <span className="font-medium">
                    {Math.min(pagination.page * pagination.pageSize, pagination.total)}
                  </span>{' '}
                  of <span className="font-medium">{pagination.total}</span> results
                </p>
              </div>
              <div>
                <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px" aria-label="Pagination">
                  <button
                    onClick={() => setPagination({...pagination, page: Math.max(1, pagination.page - 1)})}
                    disabled={pagination.page === 1}
                    className={`relative inline-flex items-center px-2 py-2 rounded-l-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 ${
                      pagination.page === 1 ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    <span className="sr-only">Previous</span>
                    <ChevronLeftIcon className="h-5 w-5" aria-hidden="true" />
                  </button>

                  {/* Page numbers */}
                  {[...Array(pagination.totalPages).keys()].map((pageIndex) => {
                    const pageNumber = pageIndex + 1;
                    // Show only a limited number of page buttons
                    if (
                      pageNumber === 1 ||
                      pageNumber === pagination.totalPages ||
                      (pageNumber >= pagination.page - 1 && pageNumber <= pagination.page + 1)
                    ) {
                      return (
                        <button
                          key={pageNumber}
                          onClick={() => setPagination({...pagination, page: pageNumber})}
                          className={`relative inline-flex items-center px-4 py-2 border text-sm font-medium ${
                            pagination.page === pageNumber
                              ? 'z-10 bg-primary-50 border-primary-500 text-primary-600'
                              : 'bg-white border-gray-300 text-gray-500 hover:bg-gray-50'
                          }`}
                        >
                          {pageNumber}
                        </button>
                      );
                    } else if (
                      (pageNumber === pagination.page - 2 && pageNumber > 1) ||
                      (pageNumber === pagination.page + 2 && pageNumber < pagination.totalPages)
                    ) {
                      return (
                        <span
                          key={pageNumber}
                          className="relative inline-flex items-center px-4 py-2 border border-gray-300 bg-white text-sm font-medium text-gray-700"
                        >
                          ...
                        </span>
                      );
                    }
                    return null;
                  })}

                  <button
                    onClick={() => setPagination({...pagination, page: Math.min(pagination.totalPages, pagination.page + 1)})}
                    disabled={pagination.page === pagination.totalPages}
                    className={`relative inline-flex items-center px-2 py-2 rounded-r-md border border-gray-300 bg-white text-sm font-medium text-gray-500 hover:bg-gray-50 ${
                      pagination.page === pagination.totalPages ? 'opacity-50 cursor-not-allowed' : ''
                    }`}
                  >
                    <span className="sr-only">Next</span>
                    <ChevronLeftIcon className="h-5 w-5 transform rotate-180" aria-hidden="true" />
                  </button>
                </nav>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default TableView;
