import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMsal, useIsAuthenticated } from '@azure/msal-react';
import { loginRequest } from '../authConfig';

const Login = () => {
  const { instance } = useMsal();
  const isAuthenticated = useIsAuthenticated();
  const navigate = useNavigate();
  const [loginError, setLoginError] = useState(null);

  useEffect(() => {
    console.log('Authentication status:', isAuthenticated);
    if (isAuthenticated) {
      console.log('User is authenticated, navigating to home');
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  const handleLogin = () => {
    console.log('Login button clicked');
    console.log('MSAL config:', instance.config);

    try {
      // Use redirect flow instead of popup
      instance.loginRedirect(loginRequest)
        .catch(error => {
          console.error('Login redirect failed:', error);
          setLoginError(error.message);
        });
    } catch (error) {
      console.error('Exception during login attempt:', error);
      setLoginError(error.message);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            Data Entry Application
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            Sign in with your Azure AD account to access the application
          </p>
        </div>
        {loginError && (
          <div className="rounded-md bg-red-50 p-4 mt-4">
            <div className="flex">
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">Login Error</h3>
                <div className="mt-2 text-sm text-red-700">
                  <p>{loginError}</p>
                </div>
              </div>
            </div>
          </div>
        )}
        <div className="mt-8 space-y-6">
          <button
            onClick={handleLogin}
            className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-primary-600 hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
          >
            Sign in with Microsoft
          </button>
          <div className="text-center mt-4">
            <p className="text-sm text-gray-600">
              After clicking sign in, you'll be redirected to the Microsoft login page.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
