import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useMsal } from '@azure/msal-react';
import { toast } from 'react-hot-toast';
import { apiConfig } from '../authConfig';
import { getAccessToken } from '../services/authService';
import { TableCellsIcon } from '@heroicons/react/24/outline';

const Dashboard = () => {
  const { instance, accounts } = useMsal();
  const [tables, setTables] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTables = async () => {
      try {
        const accessToken = await getAccessToken(instance, accounts[0]);

        const response = await fetch(`${apiConfig.baseUrl}${apiConfig.endpoints.tables}`, {
          headers: {
            'Authorization': `Bearer ${accessToken}`
          }
        });

        if (!response.ok) {
          throw new Error(`Error fetching tables: ${response.statusText}`);
        }

        const data = await response.json();
        setTables(data);
      } catch (error) {
        console.error('Failed to fetch tables:', error);
        toast.error('Failed to load tables. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchTables();
  }, [instance, accounts]);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div>
      <div className="pb-5 border-b border-gray-200 sm:flex sm:items-center sm:justify-between">
        <h3 className="text-lg leading-6 font-medium text-gray-900">Available Tables</h3>
      </div>

      {tables.length === 0 ? (
        <div className="mt-6 text-center">
          <p className="text-gray-500">No tables available for your account.</p>
        </div>
      ) : (
        <div className="mt-6 grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
          {tables.map((table) => (
            <Link
              key={table.id}
              to={`/table/${table.name}`}
              className="block bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow duration-300"
            >
              <div className="p-5">
                <div className="flex items-center">
                  <div className="flex-shrink-0 bg-primary-100 rounded-md p-3">
                    <TableCellsIcon className="h-6 w-6 text-primary-600" aria-hidden="true" />
                  </div>
                  <div className="ml-5">
                    <h3 className="text-lg font-medium text-gray-900">{table.name}</h3>
                    {table.description && (
                      <p className="mt-1 text-sm text-gray-500">{table.description}</p>
                    )}
                  </div>
                </div>
              </div>
              <div className="bg-gray-50 px-5 py-3">
                <div className="text-sm">
                  <span className="font-medium text-primary-600 hover:text-primary-700">
                    View and edit data
                  </span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
